void imprimirEnPosicion(int x, int y, const string& texto) {
    HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);
    COORD pos;
    pos.X = x;
    pos.Y = y;
    SetConsoleCursorPosition(hConsole, pos);
    cout << texto;
}
void setColor(int color) {
    SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), color);
}
void malo(int x, int y) {
    int const FILAS = 3;
    int const COLUMNAS = 5;
    int mapa[FILAS][COLUMNAS] =
    {
        {0, 0, 1, 0, 0},
        {2, 0, 1, 0, 2},
        {1, 1, 1, 1, 1}
    };


    HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);

    for (int f = 0; f < FILAS; f++) {
        for (int c = 0; c < COLUMNAS; c++) {
            COORD pos;
            pos.X = x + c;
            pos.Y = y + f;
            SetConsoleCursorPosition(hConsole, pos);

            if (mapa[f][c] == 0) Console::ForegroundColor = ConsoleColor::Black;
            if (mapa[f][c] == 1) Console::ForegroundColor = ConsoleColor::DarkRed;
            if (mapa[f][c] == 2) Console::ForegroundColor = ConsoleColor::White;

            cout << (char)219;
        }
    }
}
