void lluvia() {
    int X[6] = { 65,80 ,95,110, 125, 140 };
    int Y[6] = { 35, 35, 35, 35, 35, 35 };
    int DY[6] = { 1, 1, 1, 1, 1, 1 }; // Velocidad vertical

    while (true) {
        for (int i = 0; i < 6; ++i) {
            // Borrar el asterisco en su posición actual
            cursor(X[i], Y[i]); cout << " ";

            // Rebotar dentro de los límites verticalmente
            if (Y[i] <= 16 || Y[i] >= 43) {
                DY[i] = -DY[i]; // Invertir la dirección vertical
            }

            // Mover el asterisco verticalmente según la dirección DY
            Y[i] += DY[i];

            // Dibujar el asterisco en la nueva posición
            cursor(X[i], Y[i]); cout << "*";
        }

        // Esperar antes de la siguiente actualización (10 ms)
        _sleep(100);
    }
}
