bool checkCollisionM(int x, int y, int mapa[60][200]) {
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 5; j++) {
            if (mapa[y + i][x + j] == 2) { // 2 representa hitbox en el mapa osea el color negro
                return true;
            }
        }
    }
    return false;
}
bool checkCollisionJ(int nuevaX, int nuevaY, int X[6], int Y[6]) {
    for (int i = 0; i < 6; ++i) {
        for (int j = 0; j < 6; j++) {
            if (nuevaX == X[i] && nuevaY == Y[i]) {
                return true; // Colisión detectada
            }
        }
    }
    return false; // No hay colisión
}

